

-- Generated by dbtvault.

WITH source_data AS (
    SELECT a.PRODUCT_HK, a.PRODUCT_HASHDIFF, a.PRODUCT_DESC, a.LOAD_DATETIME, a.RECORD_SOURCE
    FROM test_dbt.stg_product AS a
    WHERE a.PRODUCT_HK IS NOT NULL
),


latest_records_pre AS (
    SELECT current_records.PRODUCT_HK, current_records.HASHDIFF, current_records.LOAD_DATETIME,
           RANK() OVER (
               PARTITION BY current_records.PRODUCT_HK
               ORDER BY current_records.LOAD_DATETIME DESC
           ) AS rank
    FROM test_dbt.sat_product AS current_records
    JOIN (
        SELECT DISTINCT source_data.PRODUCT_HK
        FROM source_data
    ) AS source_records
    ON current_records.PRODUCT_HK = source_records.PRODUCT_HK
),
latest_records AS (

    SELECT *
    FROM latest_records_pre
    WHERE rank = 1
),

records_to_insert AS (
    SELECT DISTINCT stage.PRODUCT_HK, stage.PRODUCT_HASHDIFF AS HASHDIFF, stage.PRODUCT_DESC, stage.LOAD_DATETIME, stage.RECORD_SOURCE
    FROM source_data AS stage
    LEFT JOIN latest_records
    ON latest_records.PRODUCT_HK = stage.PRODUCT_HK
    WHERE latest_records.HASHDIFF != stage.PRODUCT_HASHDIFF
        OR latest_records.HASHDIFF IS NULL
)

SELECT * FROM records_to_insert